'use strict';

/**
 * Enumeration associating the key to its value. Used to make the code more legible.
 * @author Jean-Gabriel Genest
 * @since 17.10.30
 * @version 17.11.16
 */
define([], function () {
    return {
        RETURN: 8,
        ENTER: 13,
        ESCAPE: 27,
        UP: 38,
        DOWN: 40,
        RIGHT: 39,
        LEFT: 37
    };
});